import React from "react";
import './App.css';
import { BrowserRouter as Router, Link, Route, Switch,} from 'react-router-dom';
import PlaneComponent from "./components/PlaneComponent";
import IP_PlaneComponent from "./components/IP_PlaneComponent";
import IP_SpainComponent from "./components/IP_SpainComponent";
import IP_PortugalComponent from "./components/IP_PortugalComponent";
import IP_OverComponent from "./components/IP_OverComponent";
import IP_EntryComponent from "./components/IP_EntryComponent";

class App extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      showPlanesComponent: true,
      icao24ID: ""
    }
  }

  hidePlaneList(){
      this.setState({
        showPlanesComponent: false,
        showPlaneIPComponent: true
      });
  }

 hidePlaneIPList(){
      this.setState({
        showPlanesComponent: true,
        showPlaneIPComponent: false
      });
  }

  render(){

      var partial;

    if(this.state.showPlanesComponent === true){
      partial = <PlaneComponent hidePlaneList={this.hidePlaneList.bind(this)}/>;
    }
    else {
      partial = <IP_PlaneComponent hidePlaneIPList={this.hidePlaneIPList.bind(this)}/>;
    }

      return (
          <div className="App">

            <nav>
                <Router>
                <Link to="/planes">Planes</Link>
                <Route path="/planes" component={PlaneComponent} />
                <Link to="/planes/ip">IP</Link>
                <Route path="/planes/ip" component={IP_PlaneComponent} />
                </Router>
            </nav>

            {partial}
          </div>
      );
    }
}

export default App;
