import React from "react";
import axios from "axios";
import {ButtonGroup, Button, Dropdown} from 'react-bootstrap';

class IP_PlaneComponent extends React.Component {

    constructor(props){
      super(props)
        this.state = {
          planes:[]
        }
      this.loadData = this.loadData.bind(this);
    }

    componentDidMount(){
      this.loadData();
      setInterval(this.loadData, 10000);
    }

    async loadData() {
        try {
            axios.get("http://localhost:8081/ip").then(response => {
                this.setState({ planes: response.data })
            });
        } catch (e) {
            console.log(e);
        }
    }
      
    render(){
        return(
            <div>
                <h3 className="text-center"> All planes with origin in the Irebian Peninsula </h3>
          

                <Dropdown>
                    <Dropdown.Toggle variant="info" id="dropdown-basic"> Choose origin </Dropdown.Toggle>

                    <Dropdown.Menu>
                        <Dropdown.Item href="#/action-1">All planes with origin in the Irebian Peninsula </Dropdown.Item>
                        <Dropdown.Item href="#/action-2">All planes with origin in Portugal</Dropdown.Item>
                        <Dropdown.Item href="#/action-3">All planes with origin in Spain</Dropdown.Item>
                    </Dropdown.Menu>
                </Dropdown>
                <Link to={"/portugal"}>All planes with origin in Portugal</Link>

                <table className = "table table-striped">
                    <thead>
                    <tr>
                        <td>Icao24</td>
                        <td>Callsign</td>
                        <td>Origin Country</td>
                        <td>Time position</td>
                        <td>Longitude</td>
                        <td>Latitude</td>
                        <td>Altitude</td>
                        <td>Velocity</td>
                    </tr>
                    </thead>
                    <tbody>
                        { this.state.planes.map( plane =>
                        <tr key = {plane.icao}>
                            <td>{plane.icao}</td>
                            <td>{plane.callsign}</td>
                            <td>{plane.origin_country}</td>
                            <td>{plane.time_position}</td>
                            <td>{plane.longitude}</td>
                            <td>{plane.latitude}</td>
                            <td>{plane.baro_altitude}</td>
                            <td>{plane.velocity}</td>
                        </tr>
                        ) }
                    </tbody>
                </table>
            </div>
        )
    }
}

export default IP_PlaneComponent
