import React, { Component, useState, SyntheticEvent  } from "react";
//import ReactDOM from "react-dom";
import {ButtonToolbar} from 'react-bootstrap';
import axios from "axios";
import { BrowserRouter as Router, Switch, Route, Link, useRouteMatch } from "react-router-dom";
import styled from 'styled-components';
import Alert from 'react-bootstrap/Alert';
//import { GoogleMap, withScriptjs, withGoogleMap, Marker} from 'react-google-maps';
import ReactMapGL, {Marker} from 'react-map-gl';
import $ from "jquery";  

const H0 = styled.h1({
    fontSize: 25,
    paddingBottom: 30,
    paddingTop: 20,
    color: 'black',
    textAlign: "center"
});

const H1 = styled.h1({
    paddingBottom: 30,
    fontSize: 12,
    paddingTop: 0,
    textAlign: "center"
});

class PlanesGetIn extends React.Component {

    constructor(props){
      super(props);
        this.state = {
          planes:[], 
          search: '', 
          searchPlane: []
        }
      this.loadData = this.loadData.bind(this);
    }

    componentDidMount(){
      this.loadData();
      setInterval(this.loadData, 10000);
      
        $('#myInput').on('keyup', function () {
        var value = $(this).val().toLowerCase();
        $("#myTable tr").filter(function () {
            $(this).toggle($(this).text().toLowerCase().indexOf(value) > -1)
        });
      });
    }

    async loadData() {
        try {
            axios.get("http://localhost:8081/getin").then(response => {
                this.setState({ planes: response.data })
            });
        } catch (e) {
            console.log(e);
        }
    }
    
    buttonOnClick(planeIdentifier) {
      window.open(`/${planeIdentifier}`,"_blank");
    }
    
    mySubmitHandler = (event) => {
        event.preventDefault();
        const p = this.state.search;
        
        if (p === "") {
            console.log("OLA");
        
        } else {
            console.log("OLEEE " + p);
            
        }    
    }
    
    myChangeHandler = (event) => {
        this.setState({search: event.target.value});
    }
  
    render(){
        return(
            <div>
                <H0 className="text-center" > Planes that  <b>get in</b> <b>Irebian Peninsula</b> </H0>

                <input id="myInput" class="prompt" type="text" placeholder="Search..." />
                <span style={{float: "right"}}>Planes in Iberian Peninsula: {this.state.planes.length}</span>
                <table className = "table table-striped">
                    <thead>
                        <tr>
                            <td>Icao24</td>
                            <td>Origin Country</td>
                            <td>Longitude</td>
                            <td>Latitude</td>
                        </tr>
                    </thead>
                    <tbody id="myTable"> { this.state.planes.map( plane =>
                        <tr key = {plane.icao}>
                            <td>{plane.icao}</td>
                            <td>{plane.origin_country}</td>
                            <td>{plane.longitude}</td>
                            <td>{plane.latitude}</td>
                        </tr>
                    )
                }
              </tbody>
          </table>
        </div>
      );
    }
}

class AlertaVerde extends React.Component {
  render() {
    return (
        <div>
            <Alert variant="success">
                <Alert.Heading style={{ fontSize: 20}}><b>ALERT</b>!</Alert.Heading>
                <p>The <b>{this.props.brand}</b> plane just entered the Iberian Peninsula.</p>   
            </Alert>
        </div>

    );
  }
}

class AlertaVermelho extends React.Component {
  render() {
    return (
        <div>
            <Alert variant="danger">
                <Alert.Heading style={{ fontSize: 20}}><b>ALERT</b>!</Alert.Heading>
                <p>The <b>{this.props.brand}</b> plane just left the Iberian Peninsula.</p> 
            </Alert>
        </div>

    );
  }
}


export default PlanesGetIn
