import React, { Component, useState } from "react";
import ReactDOM from "react-dom";
import {ButtonToolbar} from 'react-bootstrap';
import {ModalComponent} from './ModalComponent';
import axios from "axios";
import { BrowserRouter as Router, Switch, Route, Link, useRouteMatch } from "react-router-dom";
import styled from 'styled-components';
import Alert from 'react-bootstrap/Alert';
//import { GoogleMap, withScriptjs, withGoogleMap, Marker} from 'react-google-maps';
import ReactMapGL, {Marker} from 'react-map-gl';

const H0 = styled.h1({
    fontSize: 25,
    paddingBottom: 30,
    paddingTop: 20,
    color: 'black',
    textAlign: "center"
});

const H1 = styled.h1({
    paddingBottom: 30,
    fontSize: 12,
    paddingTop: 0,
    textAlign: "center"
});



function Palha(props) {
    const [viewport, setViewPort] = useState({ 
        latitude: 39.983011, 
        longitude: -4.087557, 
        width: "72vw", 
        height:"52vh",
        zoom: 5
    });
  
    
    {this.state.planes.map((plane) => (
        <Marker key={plane.icao} latitude={plane.latitude} longitude={plane.longitude} />
        )
    )}
    
    const markers = React.useMemo(() => data.map(
    city => (
      <Marker key={city.name} longitude={city.longitude} latitude={city.latitude} >
        <img src="pin.png" />
      </Marker>
    )
  ), [props.data]);

  return (
    <ReactMapGL {...viewport} width="100vw" height="100vh" onViewportChange={setViewport}>
      {markers}
    </ReactMapGL>
  );
}

class IP_OverComponent extends React.Component {

    constructor(props){
      super(props);
        this.state = {
          planes:[],
          addModalShow: false
        }
      this.loadData = this.loadData.bind(this);
    }

    componentDidMount(){
      this.loadData();
//      setInterval(this.loadData, 10000);
    }

    async loadData() {
        try {
            axios.get("http://localhost:8081/over").then(response => {
                this.setState({ planes: response.data })
            });
        } catch (e) {
            console.log(e);
        }
    }
    
    buttonOnClick(planeIdentifier) {
      window.open(`/${planeIdentifier}`,"_blank");
    }
      
    render(){       
        let addModalClose = () => this.setState({addModalShow:false});
        
        const verd = "RITA";
        const verm = "AMANTE";
        
        
        return(
            <div>
                <H0 className="text-center" > All planes <b>over</b> the <b>Irebian Peninsula</b> </H0>
                
                <AlertaVerde brand={verd} />
                <AlertaVermelho brand={verm} />
                
                <table className = "table table-striped">
                    <thead>
                        <tr>
                            <td>Icao24</td>
                            <td>Callsign</td>
                            <td>Origin Country</td>
                            <td>Time position</td>
                            <td>Longitude</td>
                            <td>Latitude</td>
                            <td>Altitude</td>
                            <td>Velocity</td>
                            <td>View</td>
                        </tr>
                    </thead>
                    <tbody> { this.state.planes.map( plane =>
                        <tr key = {plane.icao}>
                            <td>{plane.icao}</td>
                            <td>{plane.callsign}</td>
                            <td>{plane.origin_country}</td>
                            <td>{plane.time_position}</td>
                            <td>{plane.longitude}</td>
                            <td>{plane.latitude}</td>
                            <td>{plane.baro_altitude}</td>
                            <td>{plane.velocity}</td>
                            <td> 
                                <ButtonToolbar>
                                    <button variant ="danger" onClick={() => this.buttonOnClick(plane.icao)}> View </button>
                                </ButtonToolbar> 
                            </td>
                        </tr>
                    )
                }
              </tbody>
          </table>
        </div>
      );
    }
}

class AlertaVerde extends React.Component {
  render() {
    return (
        <div>
            <Alert variant="success">
                <Alert.Heading style={{ fontSize: 20}}><b>ALERT</b>!</Alert.Heading>
                <p>The <b>{this.props.brand}</b> plane just entered the Iberian Peninsula.</p>   
            </Alert>
        </div>

    );
  }
}

class AlertaVermelho extends React.Component {
  render() {
    return (
        <div>
            <Alert variant="danger">
                <Alert.Heading style={{ fontSize: 20}}><b>ALERT</b>!</Alert.Heading>
                <p>The <b>{this.props.brand}</b> plane just left the Iberian Peninsula.</p> 
            </Alert>
        </div>

    );
  }
}

export default IP_OverComponent
